import './topbar.css'
import {Link, useLocation} from 'react-router-dom'
import { useEffect, useState } from 'react'
import axios from 'axios'
import Post from '../post/Post';

export default function TopBar() {
    const user = false;
    const [post, setPost] = useState([])
    const {search} = useLocation()
    const [srch, setSrch] = useState('')
    

    useEffect(() =>{
        const fetchPost = async () =>{
            const res = axios.get("/posts" + srch)
            setPost(res)
        }
        fetchPost()
    },[srch])

    return (
        <div className='top'>
            <div className="topLeft">
                <Link className='link' to='/'>
                    <p>B Blog</p>
                </Link>
            </div>

            <div className="topCenter">
                <ul className="topList">
                    <li className="topListItem">
                        <Link className='link' to='/'>Home</Link>
                    </li>
                    <li className="topListItem">
                        <Link to='/about' className='link'>About</Link>
                    </li>
                    <li className="topListItem">
                        <Link to='/contact' className='link'>Contact</Link>
                    </li>
                    <li className="topListItem">
                        <Link to='/write' className='link'>Post</Link>
                    </li>
                    <li className="topListItem">{user && "Logout"}</li>
                </ul>
            </div>
            <div className="topRight">
                {Post.map(s => (
                <form className='searchBox' id='demo-2'><Link to={`/?user=${s.srch}`} className='link'><input name="searchText" type="search" placeholder='search'
                    onChange={e => {setSrch(e.target.value)}}
                /></Link></form>
                ))}
                {
                    user
                        ? (
                            <img
                                className='profile'
                                src={process.env.PUBLIC_URL + "/images/about.jpg"}
                                alt=""/>
                        )
                        : (
                            <ul className='topList'>
                                <Link
                                    to='/login'
                                    className='link'
                                    style={{
                                        margin: "0px 40px"
                                    }}>
                                    <li className='topListItem'>Login</li>
                                </Link>
                                <Link to='/register' className='link'>
                                    <li className='topListItem'>Register</li>
                                </Link>
                            </ul>
                        )
                }
            </div>
        </div>
    )
}
