import axios from 'axios';
import { useEffect, useState } from 'react';
import {useLocation} from 'react-router-dom'
import './singlepost.css'

export default function SinglePost() {
    const location = useLocation();
    const path = location.pathname.split("/")[2];
    const [post, setPost] = useState({});

    useEffect(() => {
        const getPost = async () =>{
            const res = await axios.get(`/posts/${path}`)
            setPost(res.data)
        }
        getPost()
    }, [path])

    return (
        <div className='singlePost'>
            <div className="singleImage">
            {post.photo && (
                <img
                    src= {post.photo}
                    alt=""
                    className="coverImage"/>
                    )}
            </div>
            <div className="wrapper">
                <div className="singleTitle">
                    <h1 className='textTitle'>{post.title}</h1>
                    <div className="singleButton">
                        <button className="modify"><img
                            className='modifyImage'
                            src={process.env.PUBLIC_URL + "/images/Edit.png"}
                            alt=""/></button>
                        <button className="modify"><img
                            className='modifyImage'
                            src={process.env.PUBLIC_URL + "/images/Delete.png"}
                            alt=""/></button>
                    </div>
                </div>
                <div className="singleInfo">
                    <span className="info">Author:
                        <b>{post.username}</b>
                    </span>
                    <span className="info">{new Date(post.createdAt).toDateString()}</span>
                </div>
                <p className="singleDetails">
                    {post.desc}
                </p>
            </div>
        </div>
    )
}
