import axios from 'axios'
import './sidebar.css'

export default function Sidebar() {
  const [cat, setCat] = useState([])

    useEffect(() =>{
        const getCat =async () =>{
        const res =await axios.get("/categories")
        setCat(res.data)
        }
        getCat()
    },[])
  return (
    <div className='sidebar'>
        <div className="secondSidebar">
      <div className="sidebarItem">
        <span className="sidebarTitle">ABOUT ME</span>
        <img className='about' src={process.env.PUBLIC_URL +"/images/about.jpg"} alt="" />
        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis nulla pretium sit feugiat. Eros, arcu urna lacus, quis non vel a, consequat blandit. Nullam sit mauris viverra placerat tempor, aenean cras egestas volutpat. Sapien phasellus.</p>
      </div>
        <div className="sidebarItem">
            <span className="sidebarTitle">Categories</span>
            <ul className="sidebarList">
                <li className="sidebarListItem">Life</li>
                
            </ul>
        </div>
        </div>
    </div>
  )
}
