import axios from 'axios';
import { useContext, useEffect, useState } from 'react';
import {Link, useLocation} from 'react-router-dom'
import './singlepost.css'
import edit from '../../images/Edit.png'
import del from '../../images/Delete.png'
import { Context } from '../../context/Context'

export default function SinglePost() {
    const location = useLocation();
    const path = location.pathname.split("/")[2];
    const [post, setPost] = useState({});
    const PF = "http://localhost:5000/image/"
    const {user}  = useContext(Context)
    const handleDelete = 

    useEffect(() => {
        const getPost = async () =>{
            const res = await axios.get(`/posts/${path}`)
            setPost(res.data)
        }
        getPost()
    }, [path])

    return (
        <div className='singlePost'>
            <div className="singleImage">
            {post.photo && (
                <img
                    src= {PF +post.photo}
                    alt=""
                    className="coverImage"/>
                    )}
            </div>
            <div className="wrapper">
                <div className="singleTitle">
                    <h1 className='textTitle'>{post.title}</h1>
                    {post.username === user?.username && (
                    <div className="singleButton">
                        <button className="modify"><img
                            className='modifyImage'
                            src={edit}
                            alt=""/></button>
                        <button className="modify" onClick={handleDelete}><img
                            className='modifyImage'
                            src={del}
                            alt=""/></button>
                    </div>
                    )}
                </div>
                <div className="singleInfo">
                    <span className="info">Author:
                    <Link className='link' to={`/?user=${post.username}`}>
                        <b>{post.username}</b>
                        </Link>
                    </span>
                    <span className="info">{new Date(post.createdAt).toDateString()}</span>
                </div>
                <p className="singleDetails">
                    {post.desc}
                </p>
            </div>
        </div>
    )
}
